class Watch:
    def __init__(self, hora=0, minuto=0, segundo=0):
        self.__hora = 0
        self.__minuto = 0
        self.__segundo = 0
        self.setHora(hora)
        self.setMinuto(minuto)
        self.setSegundo(segundo)

    def setHora(self, valor):
        if 0 <= valor <= 23:
            self.__hora = valor
        else:
            print("fail: hora invalida")

    def setMinuto(self, valor):
        if 0 <= valor <= 59:
            self.__minuto = valor
        else:
            print("fail: minuto invalido")

    def setSegundo(self, valor):
        if 0 <= valor <= 59:
            self.__segundo = valor
        else:
            print("fail: segundo invalido")

    def getHora(self):
        return self.__hora

    def getMinuto(self):
        return self.__minuto

    def getSegundo(self):
        return self.__segundo

    def nextSecond(self):
        self.__segundo += 1
        if self.__segundo == 60:
            self.__segundo = 0
            self.__minuto += 1
            if self.__minuto == 60:
                self.__minuto = 0
                self.__hora += 1
                if self.__hora == 24:
                    self.__hora = 0

    def __str__(self):
        return f"{self.__hora:02d}:{self.__minuto:02d}:{self.__segundo:02d}"


# >>> O objeto precisa ser criado ANTES do loop
watch = Watch(0, 0, 0)

while True:
    try:
        line = input().strip()
    except EOFError:
        break

    if line == "$end":
        break

    if line == "$show":
        print(watch)
    elif line.startswith("$set"):
        _, h, m, s = line.split()
        watch.setHora(int(h))
        watch.setMinuto(int(m))
        watch.setSegundo(int(s))
    elif line.startswith("$init"):
        _, h, m, s = line.split()
        watch = Watch(int(h), int(m), int(s))
    elif line == "$next":
        watch.nextSecond()
